const App = () => {
  const [ persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ]) 

  const [ newName, setNewName ] = useState('')

  const addContact = (event) => {
    event.preventDefault();
    const personObject = {
        name: newName
    }
    setPersons(persons.concat(personObject));
    setNewName('');
  }

  const handleChange = (event) => {
    setNewName(event.target.value);
  }

  return (
    <div>
      <h2>Phonebook</h2>
